from django.core.management.base import BaseCommand
from django.db import connection


class Command(BaseCommand):
    help = 'Reseta a sequência para a tabela estoque_produto'

    def handle(self, *args, **options):
        nome_tabela = 'estoque_produto'
        with connection.cursor() as cursor:
            cursor.execute("DELETE FROM sqlite_sequence WHERE name = %s", [nome_tabela])

        self.stdout.write(self.style.SUCCESS(f'Sequência redefinida para a tabela {nome_tabela}.'))
